import GlobalComponent from '@/vueExtend/index'
//定义全局公共方法
import CommonFun from './common/CommonFun' //引用文件

console.log('plugin.js');
const MyPlugin={};
MyPlugin.install = function (Vue, options) {
    Vue.prototype.$message2 = GlobalComponent.Message2.install;
    Vue.prototype.$message1 = GlobalComponent.Message1.install;

    Vue.prototype.CommonFun = CommonFun //挂载到Vue实例上面

    //全局混入
    Vue.mixin({
        created: function () {
            //每个vue实例对象 都赋给 window下的全局变量,可以测试用;
            //如果每个组件的name都严格按照 src下的 文件目录+文件名来命名,则可把所有vue实例赋给全局变量,每个组件都能方便使用其他vue实例
            window['___' + this.$options.name] = this;
        }
    })

    // 注册一个全局自定义指令 `v-focus`
    Vue.directive('focus', {
        // 当被绑定的元素插入到 DOM 中时……
        inserted: function (el) {
            // 聚焦元素
            el.focus()
        }
    })

    Vue.prototype.$log = function (info) {
        console.log(info)
    }
    //可以在控制台使用Vue变量,测试代码之类的,比如注册 一个全局组件 anchored-heading ,然后其他组件 模板中有                 <component :level="singleSelected*1" :is="component1">标题</component>
    //修改 这个组件的component1="anchored-heading" 就能改变页面的内容
    window.Vue = Vue;


    // 1. 添加全局方法或属性
    Vue.myGlobalMethod = function () {
        // 逻辑...
    }

    // 2. 添加全局资源
    Vue.directive('my-directive', {
        bind(el, binding, vnode, oldVnode) {
            // 逻辑...
        }

    })

    // 3. 注入组件
    Vue.mixin({
        created: function () {
            // 逻辑...
        }

    })

    // 4. 添加实例方法
    Vue.prototype.$myMethod = function (methodOptions) {
        // 逻辑...
    }
}
export default MyPlugin
